from Crypto.Util.number import *
from secret import flag

p = getPrime(512)
q = getPrime(512)
n = p*q
e = getPrime(32)
c = pow(bytes_to_long(flag),e,n)
u = inverse(q,p)

print("n =",n)
print("e =",e)
print("c =",c)
print("u =",u)

